msg.payload = {
    temp: msg.payload,
    unit: 'F'
};

msg.datadir = 'pitemps';

return msg;

msg.payload = {
    on: msg.payload
}

msg.datadir = 'glycol';

return msg;

const [kegerator_demand, fermentor_1_demand] = await flow.get(['kegerator_demand', 'fermentor_1_demand']);
const [kegerator_min_temp, kegerator_max_temp, kegerator_cur, fermentor_1_min_temp, fermentor_1_max_temp, f1_cur] = await global.get(['kegerator_min_temp', 'kegerator_max_temp', 'cur_temp_smooth_kegerator', 'fermentor_1_min_temp', 'fermentor_1_max_temp', 'cur_temp_smooth_fermentor_1'])
msg.payload = {
    kegerator_demand,
    fermentor_1_demand, 
    setpoints: {kegerator: {min: kegerator_min_temp, max: kegerator_max_temp, cur: kegerator_cur}, fermentor_1: {min: fermentor_1_min_temp, max: fermentor_1_max_temp, cur: f1_cur}}};
return msg;

const {topic} = msg;
const scopes = topic.split('/');
const setpointAccessor = (minMax) => `cooling/setpoints/${minMax}/${scopes[scopes.length-1]}`;
const [min, max] = await global.get([setpointAccessor('min'), setpointAccessor('max')]);

msg.payload = false;

if (isNaN(min) || isNaN(max)) {
    return msg;
}

switch (scopes[2]) {
    case 'min':
        if (max <= min) {
            await global.set(setpointAccessor('max'), min + 0.1);
            msg.payload = true;
        }
        break;
    case 'max':
        if (min >= max) {
            await global.set(setpointAccessor('min'), max - 0.1);
            msg.payload = true;
        }
        break;
}

return msg;

const start = new Date(await flow.get('pulse_cycle_start')).getTime();
const end = new Date().getTime();
msg.payload = Math.round((end-start)/1000);
return msg;

const {namespace, topic, payload, wait} = msg;
msg.topic = namespace;
const promise = global.set(namespace, payload);

if (wait) {
    await promise;
}

return msg;

const {namespace} = msg;

msg.payload = await global.get(namespace);
msg.topic = namespace;

return msg;

const maybeSubDir = msg.datadir ? msg.datadir + '/' : '';
const fileBase = msg.topic || 'log';
const fileDate = new Date().toISOString().slice(0, 10);
const ext = '.json';
msg.filename = `/home/casey/data/${maybeSubDir}${fileBase}-${fileDate}${ext}`;

return msg;

const ts = msg.ts ? new Date(msg.ts) : new Date();

msg.payload.ts = ts.toISOString();

return msg;

const {namespace, scope, wait} = msg;

if (!namespace) {
    msg.namespace = scope;
} else {
    msg.namespace+=`/${scope}`;
}

return msg;

const {scope} = msg;

msg.namespace = scope;
return msg;

const {memorySlot, memoryTarget} = msg;

msg.memory = msg.memory ?? {};

msg.memory[memorySlot] = msg[memoryTarget];

return msg;

const {memorySlot, memoryTarget} = msg;

msg[memoryTarget] = msg.memory[memorySlot];

return msg;

const {memorySlot, memoryTarget} = msg;

delete msg.memory[memorySlot];
delete msg.memoryTarget;
delete msg.memorySlot;

return msg;

const scopes = msg.topic.split('/');
msg.topic = scopes[scopes.length-1];
return msg;
